import { Row, Col } from "antd";
import React from "react";
import { Card } from 'antd';
import Image1 from "../../assets/images/C.png"
import Image2 from "../../assets/images/react.png"
import Image3 from "../../assets/images/flutterjpg.jpg"
import Image4 from "../../assets/images/python.svg"
import Image5 from "../../assets/images/java.svg"

const { Meta } = Card;

function AppFeature() {
    // Resimler ve açıklamaları bir liste içinde tanımla
    const features = [
        { image: Image1, title: "C", description: "C programlama dili", titlePosition: 'top', descriptionPosition: 'bottom' },
        { image: Image2, title: "React", description: "React framework", titlePosition: 'bottom', descriptionPosition: 'top' },
        { image: Image3, title: "Flutter", description: "Flutter framework", titlePosition: 'top', descriptionPosition: 'bottom' },
        { image: Image4, title: "Python", description: "Python programlama dili", titlePosition: 'bottom', descriptionPosition: 'top' },
        { image: Image5, title: "Java", description: "Java programlama dili", titlePosition: 'top', descriptionPosition: 'bottom' }
    ];

    return (
        <div className="block featureBlock bgGray">
            <div className="container-fluid">
                <Row gutter={[16, 16]}>
                    {/* features listesini map fonksiyonu ile döngüye al ve her bir öğe için bir Card bileşeni oluştur */}
                    {features.map((feature, index) => (
                        <Col key={index} span={8}>
                            <Card
                                hoverable
                                cover={<img alt={feature.title} src={feature.image} />}
                                style={{ width: '100%' }}
                            >
                                {/* Başlık ve açıklama pozisyonlarını features listesinde tanımlanan titlePosition ve descriptionPosition ile belirliyoruz */}
                                {feature.titlePosition === 'top' && <Meta title={feature.title} />}
                                <div style={{ textAlign: 'center', marginTop: 'auto', marginBottom: 'auto' }}>
                                    <img alt={feature.title} src={feature.image} style={{ maxWidth: '100%', maxHeight: '100%' }} />
                                </div>
                                {feature.descriptionPosition === 'top' && <Meta description={feature.description} />}
                            </Card>
                        </Col>
                    ))}
                </Row>
            </div>
        </div>
    );
}

export default AppFeature;
