import React, { useEffect } from 'react';
import { BackTop } from 'antd';
import 'antd/dist/antd.css'; // Ant Design CSS dosyasını dahil etmeyi unutmayın
import './AppFooter.css'; // Özel CSS dosyanız

function AppFooter() {
    useEffect(() => {
        const handleScroll = () => {
            const goTopButton = document.querySelector('.goTop');
            if (goTopButton) {
                if (window.scrollY > 100) {
                    goTopButton.classList.remove('hidden');
                } else {
                    goTopButton.classList.add('hidden');
                }
            }
        };

        window.addEventListener('scroll', handleScroll);

        // Clean up
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <BackTop>
            <div className="goTop hidden"><i className="fas fa-arrow-circle-up"></i></div>
        </BackTop>
    );
}

export default AppFooter;
