import React from 'react';
import './index.css';
import { Carousel, Button } from 'antd';

const items = [
  { key: 1, title: 'Title 1', content: 'Content 1' },
  { key: 2, title: 'Title 2', content: 'Content 2' },
  { key: 3, title: 'Title 3', content: 'Content 3' },
  { key: 4, title: 'Title 4', content: 'Content 4' }
];

const App = () => {
  const onChange = (currentSlide: n) => {
    console.log(currentSlide);
  };

  return (
    <div id="hero" className="heroBlock">
      <Carousel afterChange={onChange} autoplay>
        {items.map(item => (
          <div key={item.key} className="container-fluid">
            <div className="content">
              <h3>{item.title}</h3>
              <p>{item.content}</p>
              <div className="btnHolder">
                <Button type="primary" size="large">Learn More</Button>
                <Button size="large"><i className="fas fa-desktop"></i> Watch a Demo</Button>
              </div>
            </div>
          </div>
        ))}
      </Carousel>
    </div>
  );
};

export default App;
