import React from 'react';
import { Button, Carousel, Row, Col } from 'antd';
import towerdefense from '../../assets/images/towerdefense.jpeg';
import background5 from '../../assets/images/background5.jpg'; // Arka plan resmi

const items = [
  {
    key: '1',
    title: "Arkadaşlarımla birlikte Flutter ile 'Tower Defense' adında bir oyun geliştirdik.",
    image: towerdefense,
    url: 'https://your-url-1.com'
  },
  {
    key: '2',
    title: 'Python, C, 8086 Assembly, Java, Flutter, Dart, JavaScript, HTML ve CSS programlama dillerini öğrendim.',
    url: 'https://your-url-2.com'
  },
  {
    key: '3',
    title: '',
    content: '',
    url: 'https://your-url-3.com'
  },
  {
    key: '4',
    title: '',
    content: '',
    url: 'https://your-url-4.com'
  }
];

function AppHero() {
  const handleLearnMoreClick = (url) => {
    window.location.href = url;
  }

  return (
    <div
      id="hero"
      className="heroBlock"
      style={{
        backgroundImage: `url(${background5})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        height: '100vh'
      }}
    >
      <Carousel>
        {items.map((item, index) => {
          return (
            <div key={item.key} className="container-fluid">
              <Row align="middle">
                <Col xs={24} md={12}>
                  <div className="content">
                    <h3>{item.title}</h3>
                    <p>{item.content}</p>
                    <div className="btnHolder">
                      {index === 0 && (
                        <Button type="primary" size="large" onClick={() => handleLearnMoreClick()}>Learn More</Button>
                      )}
                    </div>
                  </div>
                </Col>
                <Col xs={24} md={12}>
                  <div className="image-content">
                    <img src={item.image} alt={item.title} style={{ width: '95%', height: '50%' }} />
                  </div>
                </Col>
              </Row>
            </div>
          );
        })}
      </Carousel>
    </div>
  );
}

export default AppHero;
