import React from 'react';
import { Button } from 'antd';
import { Carousel } from 'antd';
import picture from '../../assets/images/picture.png';

const items = [
  {
    key: '1',
    title: "Arkadaşlarımla birlikte Flutter ile 'Tower Defense' adında bir oyun geliştirdik.",
    url: 'https://your-url-1.com'
  },
  {
    key: '2',
    title: 'Python, C, 8086 Assembly, Java, Flutter, Dart, JavaScript, HTML ve CSS programlama dillerini öğrendim.',
    url: 'https://your-url-2.com'
  },
  {
    key: '3',
    title: "",
    content: '',
    url: 'https://your-url-3.com'
  },
  {
    key: '4',
    title: '',
    content: '',
    url: 'https://your-url-4.com'
  }
];

//projects

function AppHero() {
  const handleLearnMoreClick = (url) => {
    window.location.href = url;
  }

  return (
    <div id="hero" className="heroBlock">
      <Carousel>
        {items.map((item, index) => {
          return (
            <div key={item.key} className="container-fluid">
              <div className="content">
                <h3>{item.title}</h3>
                <p>{item.content}</p>
                <div className="btnHolder">
                  {index === 0 && (
                    <Button type="primary" size="large" onClick={() => handleLearnMoreClick(item.url)}>Learn More</Button>
                  )}
                </div>
              </div>
              <div className="image-content">
                <img src={item.image} alt={item.title} />
              </div>
            </div>
          );
        })}
      </Carousel>
    </div>
  );
}

export default AppHero;
